package com.dataservlet.controller;

import java.io.*;
import java.util.*;

import javax.servlet.*;
import javax.servlet.annotation.*;
import javax.servlet.http.*;

/**
 * Servlet implementation class TestPerson
 */
@WebServlet("/testPerson.do")
public class TestPerson extends HttpServlet {
	private static final long serialVersionUID = 1L;
       
    /**
     * @see HttpServlet#HttpServlet()
     */
    public TestPerson() {
        super();
        // TODO Auto-generated constructor stub
    }

	/**
	 * @see HttpServlet#doGet(HttpServletRequest request, HttpServletResponse response)
	 */
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		//response.getWriter().append("Served at: ").append(request.getContextPath());
		request.setCharacterEncoding("utf-8");
		//1. 단일값 받기 -> request.getParameter()
		String name = request.getParameter("name");
		int age = Integer.parseInt(request.getParameter("age"));
		Double height = Double.parseDouble(request.getParameter("height"));
		String color = request.getParameter("color");
		String animal = request.getParameter("animal");
		
		//2. 복수값 받기 -> getParameterValues() 로 받아온다 (String[] 형태로 리턴)
		//단일값도 받을 수 있음
		String[] food = request.getParameterValues("food");
		String foods="";
		for(String i : food) {
			foods+=i+" ";
		}
		
		// ※ key값을 모를경우
		// request.getParameterNames(); -> 데이터의 모든 key값 반환 (Enumeration 객체 ~ iterator랑 똑같음)
		Enumeration<String> key = request.getParameterNames();
		List<String[]> parameters= new ArrayList();
		
		
		//food가 String[] 형태니까 걍 나머지꺼도 같이 String[]으로 받아주는거
		//for each로 불러와버리면 인덱스 신경 안써도 되니까 이렇게 하는것
		while(key.hasMoreElements()) {
			String nkey = key.nextElement();
			parameters.add(request.getParameterValues(nkey));
		}
		for(String[] v : parameters) {
			for(String pv : v) {
				System.out.println(pv);
			}
			System.out.println();
		}
		
		// request에서 Map 형태로 key/value 가져오기
		// request.getParameterMap() -> Map<String, String[]> 형태로 리턴해줌 (parameterMap 객체)
		Map<String, String[]> map = request.getParameterMap();
		Set keyset = map.keySet();
		Iterator<String> ikey = keyset.iterator();
		while(ikey.hasNext()) {
			String k = ikey.next();
			String temp = "";
			for(String t : map.get(k)) {
				temp+=t+" ";
			}
			System.out.println(k+" "+temp);
		}
		
		
		//응답페이지 작성하기
		//클라이언트에게 응답하기 위해서 HttpServletResponse 객체 이용
		//1. response.setContentType("");
		//   매개변수로 데이터 MIME타입 / 인코딩방식을 준다.
		response.setContentType("text/html; charset=utf-8");
		
		//2. 데이터 전송을 위한 스트림 객체 사용
		// getWriter() : 문자열(문서) 전송 -> PrintWriter 객체 리턴해줌 
		// getOutputStream() : 온갖 파일들 -> ServletOutputStream 객체
		
		PrintWriter pw = response.getWriter();
		String[] tem = foods.split(" ");
		String foodlist="";
		for(String f : tem) {
			switch(f) {
			case "밥" : foodlist+="<p><img src='data:image/jpeg;base64,' width=150 height=150></p>"; break;
			case "시리얼" : foodlist+="<p><img src='data:image/jpeg;base64,' width=150 height=150></p>"; break;
			case "김치" : foodlist+="<p><img src='data:image/jpeg;base64,' width=150 height=150></p>"; break;
			case "라면" : foodlist+="<p><img src='data:image/jpeg;base64,' width=150 height=150></p>"; break;
			case "김밥" : foodlist+="<p><img src='data:image/jpeg;base64,' width=150 height=150></p>"; break;
			}
		}
		pw.write("<html>"
				+"<head>"
				+"<meta charset='UTF-8'>"
				+"</head>"
				+ "<body>"
				+ "<p> 이름 : "+name+"</p>"
				+ "<p> 나이 : "+age+"살</p>"
				+ "<p> 키 : "+height+"cm</p>"
				+ "<p style='color:"+color+"'> 좋아하는 색 : "+color+"</p>"
		        + foodlist
				+ "<p> 좋아하는 동물 : "+animal+"</p>"
				+ "<button onclick='history.back();'>뒤로가기</button> "
				+ "</body>"
				+ "</html>");

	}

	/**
	 * @see HttpServlet#doPost(HttpServletRequest request, HttpServletResponse response)
	 */
	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		// TODO Auto-generated method stub
		response.setContentType("text/html; charset=utf-8");
		doGet(request, response);
	}

}
